(2,65,'<p><strong>PLAN DE ALIMENTACION:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>ANTES DE HACER EJERCICIO: JUGO VERDE (APIO,NOPAL,PEPINO,JENGIBRE,MANZANA VERDE)</strong></p>\r\n\r\n<p><strong>O LICUADO DE MANZANA VERDE CON AVENA TODO MEZCLADO CON AGUA.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>DESAYUNO: PECHUGA ASADA O ATUN EN AGUA ACOMPA&Ntilde;ADO DE VERDURAS, TE DE MANZANILLA O HIERBABUENA SIN AZUCAR </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>COMIDA: SALMON O PESCADO AL VAPOR ACOMPA&Ntilde;ADO DE VERDURAS, ACOMPA&Ntilde;ADO DE AGUA NATURAL</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>CENA: UNA TAZA DE FRUTA ACOMPA&Ntilde;ADA DE TE </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>+ PODEMOS COMER QUESO PANELA O COTTAGE</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>+ 50 GRAMOS DE NUEZ, NUEZ DE LA INDIA SIN SAL, ARANDANOS, ALMENDRAS, CHIA, LINAZA</strong></p>\r\n\r\n<p><strong>SALVADO, QUINOA, PASAS, AMARANTO SOLO LA MEDIDA Y&nbsp; UNA COSA DE LAS MENCIONADAS AL DIA.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>- ALIMENTOS SIN SAL SIN AZUCAR</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>- NO HARINAS, NO PASTAS, NO GRASAS, NO CARNES ROJAS, NO LEGUMINOSAS, NO EMBUTIDOS</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>-</strong><strong> NO COSAS FRITAS, DORADAS, EMPANIZADAS, CAPEADAS</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>QUEDO A SUS ORDENES PARA CUALQUIER DUDA GRACIAS!!!</strong></p>\r\n\r\n<p>&nbsp;</p>','2021-04-14 20:06:41','2021-04-14 20:06:41'),
(3,91,'<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; TOMARSE SU MEDICAMENTO DE LA PRESION A SUS HORAS</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; REPORTAR PRESION ARTERIAL, TEMPERATURA Y SATURACION DE OXIGENO TRES VECES AL DIA Y REPORTAR A SU MEDICO TRATANTE HASTA EL DIA 25 DE ABRIL</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; MANTENERSE HIDRATADA 2 LITROS DE AGUA DIARIOS</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; NADA DE SAL NADA DE AZUCAR COMER ABUNDANTES VERDURAS, CARNES BLANCAS, FRUTAS REGULARES</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; AHISLAMIENTO TOTAL HASTA EL DIA 25 DE ABRIL DEL 2021</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; ESTAR EN CONSTANTE COMUNICACION CON SU MEDICO</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; SINTOMAS Y SIGNOS DE HOSPITALIZACION:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>DOLOR DE CABEZA INTENSO</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>SATURACION DE OXIGENO MENOR DEL 85%</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>VOMITO CONSTANTE</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>NO QUERER COMER NADA</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>DIFICULTAD PARA RESPIRAR</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>DOLOR DE PECHO O ESPALDA INSOPORTABLE</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>FATIGA O CANSANCIO EXCESIVO</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>FIEBRE INCONTROLABLE</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','2021-04-15 17:26:05','2021-04-15 17:26:05'),
(4,92,'<p>&deg; <strong>COMER SIEMPRE A SUS HORAS, MA&Ntilde;ANA TARDE Y NOCHE</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; MASTICAR DESPACIO Y CON CALMA DEJAR LOS LIQUIDOS AL FINAL</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; CONTROL DE ESTRES Y SITUACIONES DE PRESION QUE MAS SE PUEDA </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; DORMIR CON UNA O DOS ALMOHADAS, LA CENA ES MUY LIGERA, PASAR POR LO MENOS DOS HORAS PARA IRSE A DORMIR Y ACOSTARSE DEL LADO IZQUIERDO </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&deg; EVITAR IRRITANTES, GRASAS, HARINAS, LEGUMINOSAS, PASTAS, ARROZ, COSAS FRITAS, DORADAS, CAPEADAS O EMPANIZADAS.</strong></p>','2021-04-16 14:09:26','2021-04-16 14:09:26'),
